INSERT INTO "NoAddiction".quotes (_id, quote, author, is_bad, category_id) VALUES (1, 'Каждый год в россии около 5000 людей в возрасте до 21 года умирают в ситуациях, связанных с алкоголем, — например, в авариях, бытовых убийствах, в результате отравления и других подобных травм.', 'Nobody', true, 1);
INSERT INTO "NoAddiction".quotes (_id, quote, author, is_bad, category_id) VALUES (2, 'Какая разница кто какой национальности? Взял водку - пей!', 'Влад Легков', false, 1);
INSERT INTO "NoAddiction".quotes (_id, quote, author, is_bad, category_id) VALUES (3, 'Алкоголь – штука коварная: с одной стороны, бокал пива – просто незаменимое лекарство от перенапряжения после тяжелой рабочей недели. Но с другой – это невидимый, но достаточно ощутимый удар по здоровью, бьющий в самые уязвимые места нашего организма.', null, true, 1);